INSERT INTO breweries (brewery_name, brewery_description, image_url) VALUES ('Columbus Brewing Company', 'Columbus Brewing Company is an independent craft brewery dedicated to exploring the flavors of American hops. Best known for Columbus IPA and Bodhi, we also enjoy the subtleties of German-style lagers, the intense characteristics of barrel aged beers and everything in between. We have designed and built our new brewery to make more beer, but also to allow us to explore new frontiers.', 'https://pbs.twimg.com/media/E7aDNmRWQAAQlrE.jpg');
