INSERT INTO breweries (brewery_name, brewery_description, image_url) VALUES ('Platform Beer Co.', 'From humble beginnings, brewing on a 3 bbl brewhouse in Cleveland, Platform has expanded to a second tasting room in Columbus, a production facility in Cleveland, a Cincinnati tasting room named LOCOBA and a sour beer facility in Cleveland named Phunkenship.', 'https://d1ynl4hb5mx7r8.cloudfront.net/wp-content/uploads/2018/04/17115906/Platform.jpg');
