INSERT INTO breweries (brewery_name, brewery_description, image_url) VALUES ('Wissahickon Brewing Company', 'Wissahickon Brewing Company (WBCo) is a family owned & operated production brewery & tasting room located in East Falls, Philadelphia, PA. We’re family & pet friendly, host food trucks every day we’re open, & continue to explore new & interesting styles, ingredients, & recipes.', 'https://s3-media0.fl.yelpcdn.com/bphoto/QAkiRNk_VCrDbzTDL652VQ/l.jpg');
