INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Lost Lager', 'https://www.brewdog.com/media/catalog/product/cache/812bb802e88c6c6573092eb3de0ab6b0/l/o/lostlager_heroshot_edited_10.0_300dpi_rgb.png', 'Dry-Hopped Pilsner', 'Lost Lager is the world''s first carbon negative lager. This mind-blowingly delicious 4.5% German-style Pilsner is lager like it should be, with an extra dose of feel good for the planet. ', '4.7', '1')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Elvis Juice', 'https://cdn.shopify.com/s/files/1/2264/1213/products/ElvisJuice16oz.png?v=1612295492', 'Grapefruit IPA', 'An American IPA with a bitter edge that will push your citrus tolerance to the brink and back; Elvis Juice is loaded with tart pithy grapefruit peel. This IPA has a caramel malt base, supporting a full frontal citrus overload - grapefruit peel piled on top of intense us aroma hops. Waves of crashing pine, orange and grapefruit round out this citrus infused IPA.', '6.5', '1')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Hazy Jane', 'https://cdn.shopify.com/s/files/1/2264/1213/products/HazyJane16oz.png?v=1612295339', 'IPA', 'Hazy Jane is a Vermont style IPA, with low background bitterness, loaded with intense juicy fruit character. Pineapple, stone fruit, mango, light resin and hints of lime peel – this juicy IPA is full-bodied and smooth, enhancing the soft ripe fruit flavors.', '7.2', '1')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Pineapple Punch', 'https://cdn.shopify.com/s/files/1/2264/1213/products/Hawkes-Pineapple-Punch.png?v=1620841641', 'Hard Cider', 'The humble apple takes swings at a top-notch contender, the spiky pineapple. Following a 2-week bout in the fermenter, this unlikely pairing brings a barrage of tropical flavor. Zingy, fresh pineapple is tempered with the bright acidity of our Columbus-made cider.', '4.0', '1')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Ghostwalker', 'https://www.brewdog.com/media/catalog/product/cache/812bb802e88c6c6573092eb3de0ab6b0/c/a/can_brewdog-ghost-walker.png', 'IPA', 'Ghost Walker rocks a hop-forward flavor, containing less than 0.5% alcohol. Tropical aromas harmonize with grassy, pine notes, all sitting on a solid malt baseline, proving that non-alcoholic beer can taste just as good.  This beer is a liquid celebration of clarity in the face of adversity. We''ve muted the alcohol, but amped up the flavor. So all the thrash, all the passion, and all the attitude remain. Ghost Walker - Pure metal without the morning after bangover.', '0.5', '1')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Double Punk', 'https://www.brewdog.com/media/catalog/product/cache/812bb802e88c6c6573092eb3de0ab6b0/d/o/double_punk_500ml_can_rgb_8.2_1.0.png', 'Imperial IPA', 'Double Punk and double the fun. The beer that started it all gets ramped up to 8.5% for a relentlessly hoppy onslaught. A light malt backbone is balanced out with a citrus-forward and super pithy delivery onboard a crisp, clear golden appearance. Expect plenty of bitterness from the heavy-handed hopping that invites aromas of grapefruit and zesty fruits.', '8.5', '1')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Clockwork Tangerine', 'https://cdn.shopify.com/s/files/1/2264/1213/products/BrewDog_ClockworkTangerine_12oz_main.png?v=1601587373', 'Tangerine Session IPA', 'Clockwork Tangerine first made an appearance last year when it took the crown in our 2017 Prototype Challenge under the pseudonym of ''Tangerine Session IPA''. A flavor profile that''ll tick all those boxes as we delve into the summer months! At 4.5% it''s a standout session beer that packs a juicy citrus hit and mellow tropical notes on a toasty and caramel background.', '4.5', '1')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Hoppins All The Time', 'https://cdn.shopify.com/s/files/1/2264/1213/products/HoppinsAllTheTime16oz.png?v=1624397324', 'IPA - American', '"Hoppins all the Time" is a 6.8% West Coast IPA brewed with Citra Incognito and fermented with Berkeley Yeast Lab''s "Super Bloom" strain. This California ale yeast was genetically modified to produce terpenes (aroma compounds associated with hops and cannabis) and gives off a bouquet of lime and tangerine with notes of berry. We accentuated these aromas with a generous dry hopping of Amarillo, Citra and Simcoe hops.', '6.8', '1')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Lightspeed', 'https://beverages2u.com/wp-content/uploads/2021/01/BREWDOG-LIGHTSPEED.png', 'Hazy IPA', 'Lightspeed is a 4% lo-cal hazy IPA with just 99 calories per can. This light beer has an all-star hop profile featuring Cascade, Centennial, Citra and Nelson Sauvin conjuring flavors of apricot and peach, with hints of citrus fruits and light floral notes refracting off the delicate malt base. Lightspeed proves low calorie does not have to mean low flavor.', '4.0', '1')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Dead & Berried', 'https://craftroom.si/app/uploads/2021/01/BrewDog_Hawkes_Cider_Dead_Berried-300x300.png', 'Hard Cider', 'Packed to the rafters with blackberry, raspberry and blueberry on a brisk apple base, this super fruity cider is fermented with a wine yeast to delicately balance the sweet berry front. Deep, lush, and dead drinkable.', '6.0', '1')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('614Day Hazy IPA', 'https://images.squarespace-cdn.com/content/v1/51abeb0be4b08f6a770c06bf/1623701629007-40JU3EAO76CVLSKT9H12/DSCF1189.jpg?format=1500w', 'Hazy IPA', 'A fruity & well-rounded Hazy IPA, hopped with Lotus, Sabro, Citra and Mosaic hops.', '6.5', '2')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Luella', 'http://static1.squarespace.com/static/51abeb0be4b08f6a770c06bf/51abeb0ce4b08f6a770c06d1/606f5575af65194cb8a0fcc6/1627055441831/?format=1500w', 'Imperial Stout', 'Luella, our first ever Cherry Imperial Stout is in 4 packs now. Cherry cola meets dark chocolate. Yum.', '11.00', '2')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Goo Goo Muck', 'https://images.squarespace-cdn.com/content/v1/51abeb0be4b08f6a770c06bf/1616007544400-WEUFGABYAKDR8LPVYH75/_O5A4193.jpg?format=750w', 'IPA', 'Goo Goo Muck is a Tart IPA and is absolutely one of the top fan favorites that we make.', '6.8', '2')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Reverse Winter', 'https://images.squarespace-cdn.com/content/v1/51abeb0be4b08f6a770c06bf/1616007837622-T63N4208AB75ZMQCBCRU/_O5A4198-2.jpg?format=1500w', 'Imperial Stout', 'Imperial Rye Stout with apple brandy stout added, 12.5% ABV. Very dark with a nice rye-spice, Reverse Winter has the really cool (and tasty) addition of beer aged in apple brandy barrels from Watershed Distillery. ', '12.5', '2')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Rolled Up', 'https://images.squarespace-cdn.com/content/v1/51abeb0be4b08f6a770c06bf/1616007942006-BC4F2AYPFCTG70ZGIW45/_O5A4192.jpg?format=1500w', 'Wheat Ale', 'Fresh guava and lime meet up with a crisp wheat ale in Rolled Up. Tart and a little funky with a beautiful color, our second version of this beer turned out great!', '5.5', '2')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Seventh Son', 'https://images.squarespace-cdn.com/content/v1/51abeb0be4b08f6a770c06bf/1605557784112-RZFJBF3BW8IQ5VGBDFWQ/CO5A1526.jpg?format=1000w', 'American Strong Ale', 'The beer that started it all! Seventh Son is an American Strong Ale with a tasty malt character and light bitterness.', '7.8', '2')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Humulus Nimbus', 'https://images.squarespace-cdn.com/content/v1/51abeb0be4b08f6a770c06bf/1605557789358-DPRMTU775B8RGK43SA63/CO5A1527.jpg?format=1000w', 'IPA', 'Our most popular beer for a reason, Nimbus has great craft beer flavor and easy drinkability.', '6.6', '2')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Scientist', 'https://images.squarespace-cdn.com/content/v1/51abeb0be4b08f6a770c06bf/1605557791548-VT0JK1ASOG9XZEFK5GOA/CO5A1529.jpg?format=1000w', 'IPA', 'Our flagship IPA that is always evolving is called the Scientist. A true craft classic!', '5.6', '2')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Stone Fort', 'https://images.squarespace-cdn.com/content/v1/51abeb0be4b08f6a770c06bf/1605557785501-O4KP52NAO7E2OBR2JAVR/CO5A1523.jpg?format=1000w', 'Oat Brown Ale', 'Stone Fort is an award-winning Oat Brown Ale that we have been making since the first days of Seventh Son. Rich, tasty, and very drinkable.', '7.2', '2')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Assistant Manager Beer', 'https://images.squarespace-cdn.com/content/v1/51abeb0be4b08f6a770c06bf/1605557794461-VYUVLS3PY1L3Y3ESYNSW/CO5A1530.jpg?format=1000w', 'Pale Ale', 'We named a beer after our adorable brewery cat! Its crisp, easy drinking, and our cheapest 6 pack.', '4.8', '2')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Clear Sky', 'https://cdn.shopify.com/s/files/1/0265/6976/9007/products/ImagefromiOS_136_1024x1024@2x.jpg?v=1626455164', 'Cream Ale', 'A simple, light beer which was inspired by the clear light of a full moon in a cloudless sky. This pre-prohibition styled ale satisfies with its subtle malt notes and light hopping. Making it perfect for relaxing with your pack of friends wherever the trail leads.', '5.0', '14', '3')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Daybreak', 'https://cdn.shopify.com/s/files/1/0265/6976/9007/products/DAYBREAK_Can_3D_Single_1500x.jpg?v=1589042460', 'Cream Ale', 'Confusing to the senses in the best way possible. Daybreaks clarity and golden color belie the smooth coffee presence inside. Brewed with local One Line Coffee''s rotating house roast and the finest vanilla beans. ', '5.0', '14', '3')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Pack IPA', 'https://cdn.shopify.com/s/files/1/0265/6976/9007/products/12oz__Pack_IPA_01_1000x.png?v=1589044310', 'American IPA', 'From our pack to yours, we invite you to share and enjoy this IPA wherever your path leads you.  Brimming with Citra, Mosaic and Amarillo hoppy goodness, Pack is the perfect companion for any and all awaiting adventures.', '6.8', '70', '3')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('614 Lager', 'https://cdn.shopify.com/s/files/1/0265/6976/9007/products/614render_1000x.jpg?v=1614620827', 'Lager', 'For over a decade, (614) has shared the stories and experiences that make Columbus a place we''re proud to call home. In that spirit, we''ve partnered with Wolf''s Ridge Brewing to craft a quality beer that won''t get in the way of a good time. What will your story be? Make it a good one. #614beerstory', '4.2', '12', '3')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Dire Wolf', 'https://cdn.shopify.com/s/files/1/0265/6976/9007/products/12oz_DireWolf_Bottle_01_1024x1024@2x.jpg?v=1591025544', 'Imperial Stout', 'Dire Wolf is a fiercely bold, dark, and unrelenting. In bringing together exceptional malts from diverse corners of the world, Dire Wolf impresses the palate with a sophisticated blend of dark chocolates, espresso, caramel, bread, and oats. We offer this beer as a toast to Ohio brewmasters, both past, and present, whose unique cultural traditions helped craft a brewing community as substantial as the Dire Wolf herself.', '10.8', '3')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('IPA', 'https://i0.wp.com/columbusbrewing.com/wp-content/uploads/2020/04/cbc_ipa_can.png?resize=606%2C922&ssl=1', 'India Pale Ale', 'India Pale Ales were originally brewed to withstand long ocean voyages by increasing the quantity of hops and malt used in their Pale Ale predecessors. Our take on IPA pours pale orange with a balanced malt presence and finishes with a combination of grapefruit, pine, and tropical fruit notes from Pacific Northwest grown hops.', '6.3', '60', '4')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Bodhi', 'https://i2.wp.com/columbusbrewing.com/wp-content/uploads/2020/04/cbc_bodhi_can.png?resize=606%2C922&ssl=1', 'Double India Pale Ale', 'An awakening of the senses by virtue of American hops. Pale golden in color, this double IPA showcases a unique blend of hops, highlighting the true nature of Citra.', '8.3', '80', '4')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Skateland', 'https://i0.wp.com/columbusbrewing.com/wp-content/uploads/2019/02/cbc_skateland_bottle.png?resize=606%2C922&ssl=1', 'Blonde Pale Ale', 'The newest addition to our rotating Pale series, Skateland is a blonde pale ale featuring a slightly sweet malt profile with a balanced bitterness. The hop flavors and aromas are of tropical fruit, oranges, and stone fruit.', '5.3', '27', '4')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Grasshoppa', 'https://i1.wp.com/columbusbrewing.com/wp-content/uploads/2018/02/cbc-grasshoppa-bottle.png?resize=607%2C923&ssl=1', 'India Pale Ale', 'This deep golden session ale is brewed with balance in mind, yet still delivers the distinct American hop character we crave. Grasshoppa proudly features Pacific Northwest grown Chinook, Simcoe, and Citra hops which lend aromas of citrus, red berries, and pine.', '4.7', '45', '4')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Go-Cart Ghost', 'https://columbusbrewing.com/wp-content/uploads/2020/08/GKG-Can-V5.png', 'Pale Ale', 'This green flag has been waived and It’s time to take a few laps with our newest pale ale, Go Kart Ghost. The revved up hop additions of El Dorado® and Mosaic in this deep golden beer contribute aromas of citrus and candied stone fruit. This is a race you won’t want to end!', '6.1', '40', '4')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Thunder Lips', 'https://i1.wp.com/columbusbrewing.com/wp-content/uploads/2019/01/cbc_thunderlips_bottle.png?resize=606%2C922&ssl=1', 'Pale Ale', 'This deep golden Pale Ale is brewed with Simpsons Golden Promise malt to create a backbone for thunderous additions of Mosaic® and Idaho 7 hops.', '5.3', '40', '4')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Volume I', 'https://i1.wp.com/columbusbrewing.com/wp-content/uploads/2020/04/cbc_insane_wanderer_vol7_bottle.png?resize=606%2C922&ssl=1', 'India Pale Ale', 'Volume 1 is the first release of Insane Wanderer. This American India Pale Ale is complemented by the exploration of the unique qualities of Mosaic hops, partnered with a few of its dearest friends.  Let the journey begin.', '7.4', '75', '4')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Volume II', 'https://columbusbrewing.com/wp-content/uploads/2018/08/cbc-insane-wanderer-vol-2-bottle.png', 'Pale Ale', 'Golden in color, this American India Pale Ale bursts with aromas and flavors of ripe orchard fruit, mango, and passionfruit. In this installment, Midwestern grown Cashmere hops are highlighted, joined by Citra and Mosaic hops. Our journey continues…', '7.4', '75', '4')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Volume III', 'https://i0.wp.com/columbusbrewing.com/wp-content/uploads/2018/10/cbc-insane-wanderer-vol-3-bottle.png?resize=606%2C922&ssl=1', 'India Pale Ale', 'Golden in color, this American India Pale Ale bursts with aromas and flavors of passionfruit, peaches, and pineapples. In this installment, Australian grown Galaxy hops are highlighted, joined by Simcoe and Chinook hops. Our journey continues…. ', '7.4', '75', '4')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Insane Wanderer', 'https://i2.wp.com/columbusbrewing.com/wp-content/uploads/2020/07/cbc_insane_wanderer_vol8_bottle.png?resize=606%2C922&ssl=1', 'India Pale Ale', 'Volume VIII features a trinity of celebrated American hops. Citra, Simcoe and Mosaic represent the hallmark of the modern American IPA and the latest volume of Insane Wanderer brings the best notes of each in both flavor and aroma. Volume VIII is pale orange in color with the citrus and pine notes of Simcoe, the tropical fruit of Citra and the undeniable herbaciousness of Mosaic. Insane Wanderer brings them all together for one unforgettable experience.', '7.4', '52', '4')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Christmas Ale', 'https://abita.com/graphics/brews/846/bottle_family__0009_christmas-2__slide.png', 'Pale Ale', 'When it came time to brew Barley''s Christmas Ale though, we drop everything we''re doing, grab some chairs (and beers!) and sit around zesting oranges while we chat. We add them and fresh young ginger, whole cinammon sticks and organic honey to the brew. It''s hard not to get cheerful when doing such a special task.', '6.8', '27', '5')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Blood Thirst Wheat', 'https://pbs.twimg.com/media/E7oz5GxX0AQ0Vp3.jpg', 'IPA', 'Winner of our 14th annual Homebrew Competition in 2009. Lloyd Cicetti''s Wheat Beer with Blood Oranges is a citrusy and supremely refreshing with floral notes thanks to Sicillian blood orange zest added to the brew kettle and puree added during and after fermentation. This hefeweizen has hints of fruity esters and spice from Bavarian wheat yeast.', '4.4', '33', '5')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Pilsner', 'https://untappd.akamaized.net/photos/2021_05_07/fe3b54a4ae9a6528f924ccc6677dbd7e_640x640.jpg', 'Pilsner', 'A great introduction to craft brewed beer, since there is enough malt and hop flavor in our Pilsner to keep even the most sophisticated beer drinkers coming back for more.', '6.2', '45', '5')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Barley''s Pale Ale', 'https://i.ebayimg.com/images/g/2isAAOSw2A9ejiAj/s-l300.jpg', 'Pale Ale', 'Pale ale was the first beer we brewed in our brewery and continues to be one of our flagship ales. Because of the rich color of British malt, it is not very pale, but the rich flavor matches the color beautifully.', '4.9', '45', '5')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Barley''s Scottish Ale', 'https://untappd.akamaized.net/photos/2021_07_30/f98c136c94ef2f7b778b87fa841a84c2_640x640.jpg', 'Scottish Ale', 'When we first brewed this beer we didn''t know what would happen when we turned our brew kettle on while it was empty, in order to scorch the malt. We liked the results so much that Scottish has been on tap ever since.', '7.3', '68', '5')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('A Haze Odyssey', 'https://barleysbrewing.com/shop/wp-content/uploads/2017/06/growler-1200x1360.jpg', 'Hazy IPA', 'While Batch #2000 paid tribute to the solid foundation that British Malt and Centennial Hops contributed to Barley''s history, Batch 2014 is firmly planted in the present. The beer style "Juicy or Hazy IPA" was recognized by the Brewers Association this Spring and batch #2001 marks our humble first attempt at the style. This is our second beer brewed entirely with super crisp Ohio Malt from Haus Malts in Cleveland.', '8.8', '75', '5')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Russian Imperial Stout', 'https://lh3.googleusercontent.com/proxy/b-lOmV8WvAq_sQPLpb9iGb0o5uYalX32VvPmppNcrY4E5GlAyZ-13fbbuGyAjrH7XmgDmi1aNaNGGJ9C4u-BZG57BQcQc0UOhy7rU9Z00QlgoQnfPTNaZ89kKKxS3VdV_2lX140v-SsGhCzwl7AedANJYHq41LlC0AvL', 'Imperial Stout', 'The first time we brewed the Russian we didn''t think we''d get all the grain in the mash tun. Until Ohio liquor laws allowed us to exceed 6% alcohol by weight, this was as big as brewing workouts got here. The result is a roasty, fruity beer that you might be tempted to eat with a spoon.', '9.6', '80', '5')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Infinity Grand Cru', 'https://barleysbrewing.com/ale/beer/images/header_infinity_grandcru.jpg', 'Begian Dark Strong Ale', 'Barley''s Infinity Grand Cru is a wonderfully complex Belgian dark strong ale. Complex, with a rich malty sweetness, fruity esters and significant alcohol with moderate spiciness. The malt is rich and forward, with caramel and toast aroma. The esters give raisin, plum and dried cherry notes, with a subtle pepperiness.', '5.6', '42', '5')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Bourbon Meyer Buckeye Stout', 'https://untappd.akamaized.net/photos/2021_07_28/90e4dd2c13268fc319f38fd880948563_640x640.jpg', 'Bourbon Stout', 'Imperial stout aged in bourbon barrels with chocolate and peanut butter.', '10.8', '88', '5')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Truth', 'IPA - American', 'Intensely hopped, dry IPA with Centennial, Citra, Amarillo and Simcoe Hops. We hop this at a rate of 3lbs/bbl to rev up peach, mango and passion fruit notes.', '7.2', '75', '6');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Bubbles', 'Fruit Beer', 'A Rosé Ale made with apple, peach and cranberry, for additional pink hue, tartness and juicy fruitiness.', '6.2', '6');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Cougar', 'Blonde Ale', 'This bright blonde ale shines a brilliant malt character. Born of breezes, captured sunlight and whiffed, this beer is a balanced yet dry siren whose first sip calls for the third. Hopped with arguable restraint with Crystal and Bravo hops, Cougar stays fierce enough to raise eyebrows yet is tame enough to be called sessionable.', '4.8', '25', '6');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Dad', 'Red Ale', 'This hoppy Holiday Red Ale has a blend of hop spice, citrus and some caramel malts to raise the holiday spirit in this Pale Ale.', '6', '45', '6');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Peach Dodo', 'Sour', 'This German Gose is bright and frisky, tart and saline, and dances on the palate with punches of peach. Thirst quenching beyond all imagination. Peach Dodo is the finest of liquids for a beach picnic or for making history.', '4.4', '12', '6');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Franz', 'Märzen', 'Franz is our Oktoberfest beer. He’s an autumnal brew, a bit of a dreamer, often hikes in the Alps and comes back with great ideas for delicious malts and is forever hoisting barrels over his head for ’ze exercize’. Franz is our Oktoberfest brau, and our first take on a German brew. Not traditional, but über German. These malts will PUMP you UP, jaaah!', '5.4', '20', '6');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Knowledge', 'IPA - Imperial / Double', 'Intense & refined, Knowledge delivers notes of resinous pine, dank sap, pithy grapefruit, & ripe pineapple. Chewy hops, a zesty punch; this erudite Imperial India Pale Ale sharpens perceptive depths in the studious night.', '8.5', '98', '6');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Zen', 'IPA - Session', 'Zen is balance, the clear morning air, feeling the flow. Zen is purpose and determination and detachment. Not bitter, but hoppy and bright. This Session IPA emanates hints of orange zest and lemongrass.', '4.8', '45', '6');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Panther', 'Porter - American', 'This malty yet sneakily smooth Robust Porter simmers with tones of milk chocolate, carob and light molasses. Panther is a svelte creature of the night – a midnight malt whisperer.', '5.8', '28', '6');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Saber Tooth Tiger', 'IPA - Imperial / Double', 'Prehistorically hopped Imperial India Pale Ale that attacks the senses with potent notes of papaya, mango and peach - yet it finishes its prey (your palate) with a crisp clean citrus bitterness.', '8.5', '95', '6');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('PsycHOPathy', 'IPA - American', 'Explore the blend of citrus, bright, and floral aromas. Additional citrus flavors follow alongside a smooth bitterness. It finishes with a medium body and a slightly sweet, malty backbone. Trust your senses.', '6.9', '60', '7');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Lift', 'Kölsch', 'Whether taking a break from a hard day of work or just a huge day of hops, this crisp, refreshing beer with a hint of orange will lift your spirits without lowering your expectations for what a craft beer should be.', '4.7', '11', '7');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Happy Amber', 'Red Ale - American Amber / Red', '2018 GABF Gold Medal Winner | This dry-hopped ale combines caramel and biscuit malt flavors in happy equilibrium with late addition American hops. The approachable balance is intentional although the name is a “hoppy” accident.', '6', '30', '7');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Thundersnow', 'Scottish Ale', 'The conditions have to be just right for the formation of Thundersnow. Ginger, nutmeg and vanilla, hints of cinnamon and a bready malt sweetness create a storm of flavors. When you find this rare occurrence, savor it as long as you can.', '8.5', '18', '7');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Shade', 'Fruit Beer', 'If a shortcut through the woods beats the heat, why not take it? Though non-traditional, Shade’s blackberries and sea salt quench thirst amidst any sunny day. Feel free to kick back and relax, the ‘Tree has you covered.', '4.6', '10', '7');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Rubus Cacao', 'Stout', 'Sweet chocolate notes lead as raspberry tartness follows in unison resembling a perfectly executed dance. The result is a blend of flavors even more spectacular together than apart. It''s love at first sip.', '7', '12', '7');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('PSA', 'Pale Ale - American', 'PSA was designed to be just that, a Public Service Announcement that craft beer can be both thoughtful and approachable. You may choose to dissect the abundance of citrus, dank, and tropical fruit flavors or to simply enjoy the proper balance between malt sweetness and body that PSA delivers. Either way, you bring the friends. We''ll bring the beer. Spread the word.', '4.5', '30', '7');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Citra High', 'IPA - Imperial / Double', 'Senses reshape as an explosion of American hops bring a kaleidoscope of tropical and citrus flavors.', '9.5', '120', '7');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Galaxy High', 'IPA - Imperial / Double', 'Join two Australian hops as you encounter a crazy collection of tropical and dank flavors throughout this Imperial IPA. It’s big on your palate… disruptive even. Think "trophic cascades" disruptive.', '9.5', '120', '7');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Rounding Third', 'IPA - Red', 'Brewed to celebrate the start of our favorite pastime. Rounding Third has a slight caramel semisweet malt backbone balanced by a strong hop profile. Dry hopped with Falconers Flight and Simcoe that will have your palate rounding third and heading for home.', '6.5', '51', '7');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Scoring Discrepancies', 'Brown Ale - Imperial / Double', 'Imperial Stout with peanut butter and raspberry. In collaboration with Bottle Logic Brewing.', '11.6', '8');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Nutcase Peanut Butter Porter', 'Porter - American', 'A nice smooth porter with a back end of peanut butter happiness.', '6.8', '33', '8');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Wisconsin Stout', 'Stout - Imperial / Double', 'Strong stout with maple and lactose. Collaboration with Untitled Art.', '10', '8');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Team Fiona', 'IPA - New England', 'Fiona is a surprise dream team collaboration between us and the Cincinnati Zoo''s rising star, Fiona the Hippo! We are humbled and honored to be a part of the team that is supporting this adorable little hippo. We went with the tried and true combination of Citra and Centennial. The result is a very pleasant, quaffable tropical and citrus forward IPA with little to no bitterness.', '7.2', '8');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Shamrock Shake', 'Stout - Milk / Sweet', 'Irish Cream Stout. **Please note there is no mint in this beer**', '5.5', '25', '8');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Don''t Talk Sh!t About Norwood', 'Pale Ale - American', 'Session Pale ale with all Falconer''s Flight 7 C''s hops. Just Don''t Talk Sh*t about Norwood!', '4.2', '31', '8');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Yoda Potato Strikes Back', 'IPA - Imperial / Double', 'NE IIPA w/ Citra, Lemondrop and Galaxy.', '8', '8');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Hank the Dumpster Kitty', 'IPA - Imperial / Double', 'New England Style IIPA brewed in honor of our brewer John''s cat. Hank was a stray cat that hung out by  Hops: Apollo, Mosaic & Citra Lupulin Powder, Citra & El Dorado Resin, Citra & Mosaic & El Dorado pelletthe dumpsters outside the brewery that he rescued, hence the name.', '8.1', '8');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('The Works', 'Stout - Milk / Sweet', 'Sweet Stout brewed with chocolate, peanut butter, vanilla, powdered sugar, nuts and sprinkles. Brewed for National Pretzel Day (April 26th) which is the best day of the year. This is pretzel day!', '10.5', '8');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Satisfied?', 'Stout - Milk / Sweet', 'Stout brewed with chocolate, caramel and peanuts to satisfy your beer thirst.', '7', '8');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('The Gadget', 'Sour - Fruited', 'A Midwest Fruit Tart Ale with 1,280 lbs of blackberries, 1,280 lbs of raspberries & 30 g of vanilla beans per 30 BBL batch.', '8', '25', '9');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Keypunch', 'Sour - Fruited Gose', 'Our house Gose with key lime zest and key lime juice added to give it a tropical punch and a refreshing kick for an all around great summer beer! Only whole keylimes were used making this beer giving a bright citrusy kick to our most refreshing summer brew.', '4.3', '12', '9');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Finn', 'IPA - Sour', 'A Tart Pale Ale, which is hopped like a citrusy American Pale Ale wit a crisp tartness. Citrus & apricot hop flavors lead the charge.', '5.3', '36', '9');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Pinwheel', 'Sour - Fruited Gose', 'Gose brewed with 500 lbs of oranges, 22 lbs of sea salt, 1 lb of coriander & 30 g of vanilla beans per 30 BBL batch.', '4.5', '12', '9');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Operation Plowshare', 'Fruit Beer', 'A Midwest Fruit Tart brewed with 2,600 lbs of blackberries & 90 g of vanilla beans per 30 BBL batch.', '8', '15', '9');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Sliderule', 'Sour - Fruited Gose', 'A dark gose with 800lbs of raspberries and 7.5 gallons of liquid cacao per 30bbl batch.', '4.9', '12', '9');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Squeezebox', 'Fruit Beer', 'Strawberry Midwest Fruit Tart. Brewed with over 3,000 lbs of strawberries per 30bbl batch.', '8.6', '9');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Fire Iron', 'Fruit Beer', 'This Midwest Fruit Tart Ale is brewed with 1,500 lbs of pink guava, 500 lbs of banana, and 400 lbs of passion fruit per 30 bbl batch.', '7.7', '20', '9');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Pickle', 'Sour - Fruited Gose', 'Pickle Pickle Pickle. 1000 lbs of fresh cucumber and two pounds of fresh dill weed. Pickle Pickle Pickle.', '4.3', '11', '9');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Spyglass', 'Sour - Gruited Berliner Weisse', 'Featuring 250lbs of Lemon Peel and 200lbs of Lime Peel, this beer is balanced, light, and easy-drinking.', '4.7', '15', '9');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Tollhouse', 'Stout - American', 'Black color with moderate bitterness. Strong roasted coffee flavor and aroma with hints of chocolate.', '7.9', '49', '10');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Earth Daisy', 'IPA - Imperial / Double', 'Reddish amber color with high bitterness. Rich malt flavor with hints of caramel and a slight peppery finish. The sweet malt backbone balances the intense earthy, piney hops.', '8.9', '121', '10');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Hoy Tripel', 'Belgian Tripel', 'Bright gold color with little bitterness. A big, dense, creamy head with a full mouthfeel. Slightly fruity with a sweet finish.', '8.9', '26', '10');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Oberhausen', 'Kölsch', 'Very pale color with medium bitterness. Somewhat vinous (grape-like) with a dry finish.', '5.6', '20', '10');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Marty''s Hamilton Ave IPA', 'IPA - American', 'Orangish gold color with minimal bitterness for an IPA, but tons of aroma and flavor to complement the rich malt backbone. Six varieties of hops give this beer its bold fresh citrus and tropical fruit aroma and flavor. Hints of black pepper linger in the aftertaste.', '6', '49', '10');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('The Noid', 'Red Ale - Irish', 'Irish red with honey.', '5.6', '28', '10');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Prunus', 'Stout - Milk / Sweet', 'Milk Stout w/ Cherries. Sweet stout w/ sweet and sour cherries.', '6.6', '16', '10');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Pepo Acer', 'Pumpkin / Yam Beer', 'Pumpkin Porter w/ Maple Bacon Donuts.', '6', '29', '10');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('HuLu', 'IPA - American', 'Pale yellow color, dominated by fresh hops in all phases … bitterness (earthy and citrusy especially grapefruit), flavor and aroma (spicy and citrusy with hints of pine). There is no malt backbone to this. It is a true homage to the hop!', '5.4', '89', '10');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Pepo', 'Pumpkin / Yam Beer', 'Pumpkin Porter.', '6', '29', '10');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Speed Merchant', 'IPA - White', 'White IPA with fruity, tropical dry hops. White wheat gives a very pale color.', '6.6', '66', '11');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Haze Jude', 'IPA - New England', 'Brewed with Simcoe and Michigan Chinook, this haze-filled wonder is a pineapple bomb that drinks smooth and is citrus Juicy.', '6', '46', '11');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('New Cleveland Palesner', 'Pilsner', 'This Pilsner/Pale Ale Hybrid is a clear straw color with a crisp German malt body and refreshing Noble hop finish with floral and herbal aromas.', '5', '23', '11');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Caramelicious', 'Stout - Milk / Sweet', 'Brewed with our friends with the unmistakable pink silos, Malley’s Chocolates. This salted caramel chocolate milk stout incorporates Malley’s famed salted caramel that lends a luscious, silky smooth body and will satisfy even the most child-like sweet tooth. Overtones of vanilla, chocolate, and caramel give way to a rich and heavy body with enough warmth to keep you cozy through this interminable winter.', '7.1', '21', '11');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Yammy Yammy', 'Pumpkin / Yam Beer', 'We know the smell of fall in the air, sweet, delicious roasted yams. Flavors amplified by seasonal and complimentary spices like cinnamon, nutmeg, and clove. This ale will have you lounging by the fireside in no time.', '7.7', '16', '11');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Holiday Donut Cookie', 'Spiced / Herbed Beer', 'Say hello to our new Holiday Ale brewed with our friends at Brewnuts CLE. Sweet milk sugar, vanilla, spice, and homemade snickerdoodle cookies. Cheers to spreading more cheer!', '7', '24', '11');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Orange Blossom Gose', 'Sour - Fruited Gose', 'Gose-style sour brewed with artisanal sea salt and orange zest to be a thirst-quenching, sessionable warm weather beer.', '4.2', '9', '11');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Paranormal Hoptivity', 'IPA - American', 'A reverent imagination of the American IPA. Pale orange in color with a ghastly addition of Ekuanot and MI Cascade hops. Mild bread and caramel malt character are accompanied by big bright citrus, tropical fruit, and melon. Dank hops.', '7', '57', '11');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Red Martian', 'Sour - Fruited', 'This slushee style sour pays homage to an old summer staple with our own twists. Fermented with Raspberry, Pineapple, and Orange this thick and smooth sour hits all the fruit notes. Additions of lactose and vanilla give hints of sweetness to round out this refreshing brew.', '5.6', '8', '11');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Esther', 'Belgian Strong Dark Ale', 'This Belgian-style Christmas Ale brings flavors of honey, dark cherries, and a hint of toffee as the glass warms.', '9.5', '23', '11');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Namber Ale', 'Red Ale - American Amber / Red', 'N/A', '5.5', '12');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('The Gustavo Black IPA', 'IPA - Black / Cascadian Dark Ale', 'This brew combines 5 malts and 5 hops to make a complex yet drinkable brew.', '7.2', '12');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Tandem Cruiser', 'Shandy / Radler', 'A blend of pale lager and sparking lemonade.', '3.8', '12');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('#Kolsh', 'Kölsch', 'N/A', '5.5', '12');
