INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Harvest Wheat', 'Wheat Beer / American Pale Wheat', 'N/A', '6.5', '12');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Particle Accelerator Double IPA', 'IPA - Imperial / Double', 'N/A', '10', '100', '12');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Jamican Chris Stout', 'Stout - American', 'N/A', '7', '12');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Fireside Wheat Winter Hefeweizen', 'Hefeweizen', 'N/A', '8', '12');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Windy And Wild', 'Farmhouse Ale - Saison', 'N/A', '5.7', '12');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Christmas Ale', 'Winter Ale', 'Do not open ‘til Christmas? Whoever coined that phrase obviously hasn’t tasted Christmas Ale’s fresh honey, cinnamon, and ginger flavors.', '7.5', '30', '13');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Edmund Fitzgerald', 'Porter - American', 'Robust and complex, our Porter is a bittersweet tribute to the legendary freighter’s fallen crew—taken too soon when the gales of November came early.', '6', '37', '13');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Chillwave Double IPA', 'IPA - Imperial / Double', 'Inspired by the North Coast''s dedicated (and sometimes chilly) surf community, our fully stoked Double IPA will melt the ice in your beard and never lose its balance.', '9', '80', '13');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Dortmunder Gold', 'Lager - Dortmunder / Export', 'A classic award-winning balance of sweet malt and dry hop flavors, proudly waving the flag for Cleveland and refreshing beer drinkers everywhere since 1988.', '5.8', '30', '13');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Oktoberfest', 'Märzen', 'Prost! Our take on this classic German style is a real malt party, packed with toasty autumnal flavors perfect for hearty hoisting.', '6.5', '20', '13');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Eliot Ness', 'Lager - American Amber / Red', 'Admittedly, it’s a bit of a paradox to name our Amber Lager for history’s most famous agent of prohibition. But it’s a smooth, malty (and dare we say, arresting?) paradox.', '6.1', '27', '13');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Commodore Perry IPA', 'IPA - English', 'What’s this? A British-style IPA named after the man who defeated His Majesty’s Royal Navy in the War of 1812? Consider this a bold, hoppy (and mildly ironic) plunder of war.', '7.7', '70', '13');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Burning River Pale Ale', 'Pale Ale - American', 'Greetings from Cleveland! Where an infamous river fire rekindled appreciation for our region’s natural resources (like the malt and hops illuminating this Pale Ale).', '6', '45', '13');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Conway''s Irish Ale', 'Red Ale - Irish', 'A pint for Pa Conway! Our co-owners’ grandfather and policeman who’d likely uphold that an Irish Ale with full-bodied caramel malt flavors is just the ticket.', '6.3', '25', '13');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Lake Erie Monster', 'IPA - Imperial / Double', 'Issue a small craft advisory: this South Bay Bessie-inspired brew unleashes a beastly hop attack amid torrid tropical fruit flavors.', '9.5', '80', '13');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('IPA', 'IPA - American', 'This IPA is the culmination of hundreds of recipe iterations over the last six years. You’ll be met upfront with aromas of grapefruit and passionfruit, accompanied by a smooth mouthfeel, soft bitterness, and tropical fruit flavors.', '6.8', '70', '14');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Single Origin Coffee Stout (Ethiopian Yirgacheffe)', 'Stout - Coffee', 'We partnered with our friends at Six Shooter Coffee to bring you this mash-up of our two favorite beverages. The lightly roasted Ethiopian Yirgacheffe coffee beans used in this beer bring flavors and aromas of berry and cocoa that pair perfectly with the roasty notes from the stout.', '6.9', '30', '14');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Tire Swing', 'Pale Ale - New England', 'This hazy pale ale is perfect for spring. Bursting with notes of citrus and stone fruit, this beer has just enough bitterness, hop flavor and aroma to keep it quaffable.', '5.5', '14');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Falling Fruit', 'Fruit Beer', 'We set out to create a beer that showcases two of our favorite fruits: tart apples and sweet peaches. We fermented it on the fruit with one of our favorite yeasts to give it a juicy character, pleasant body, and just enough sweetness to evoke memories of biting into a fresh peach or apple.', '5.8', '20', '14');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Extra! Extra!', 'IPA - Imperial / Double New England', 'Hot off the press is Extra! Extra!, one of our favorite Double New England IPAs. This beer was double dry hopped with some of our favorite American hop varieties to push the flavor and aroma over the top.', '8', '14');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Sleigh All Day IPA with Spruce Tips', 'IPA - Red', 'This seasonal IPA was brewed with a blend of spruce tips and hops yielding a balance between tropical hop goodness, piney dankness, and a brilliant malt backbone that complements all of our favorite parts of the holidays. Enjoy this beer with your loved ones while roasting chestnuts over an open fire, listening to your uncle''s best dad jokes, or to simply celebrate the season.', '7.5', '14');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Haunted Hayride Pumpkin Ale', 'Pumpkin / Yam Beer', 'Our house pumpkin ale is brewed with pumpkin, cinnamon, nutmeg and ginger.', '7.3', '14');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Flat Earth Double NEIPA', 'IPA - Imperial / Double New England', 'We threw brewing science to the curb with this beer and added an unbelievably stupid amount of hops at every step of the process. Logic and reason need not apply. Just stop reading this, believe us that it''s good, and drink it already.', '8', '14');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('A Series of Tubes', 'IPA - New England', 'A hazy, soft, and juicy IPA carefully crafted to showcase the tropical notes of these specially-processed hops. We hope you enjoy it with good company and remember, a brewery is really just a series of tubes.', '6.7', '35', '14');
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Paper Boy', 'IPA - New England', 't''s Saturday and you''re standing on the porch waiting for your newspaper. As the Paper Boy rides by, you expect the news, but instead you find yourself getting smacked in the face with your favorite American hops. This beer is the culmination of what we have learned brewing New England Style IPA.', '7.3', '14');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Evil Motives IPA', 'IPA - American', 'Double dry-hopped IPA, brewed with big hop additions of Citra, Simcoe, and Mosaic. Smooth bitterness, big aroma, and a clean finish.', '7.2', '68', '15');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Kapitän Alt Bier', 'Altbier', 'Altbier is a copper colored German-style ale with a moderate hop character. A traditional decoction mash brings out rich malt flavors without sweetness and makes Kapitän an easy drinker. A favorite of the Brewmaster!', '4.7', '38', '15');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Catchweight Kolsch', 'Kölsch', 'A light German-style ale. Want a Bud Light? Too bad. you can have this and it won''t suck. Crisp, dry, and smooth.', '4.5', '20', '15');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Union Pils Bohemian Lager', 'Pilsner - Czech', 'Traditionally brewed with a double-decoction mash to enhance malt flavor and balanced with Czech Saaz hops for a delightfully floral and spicy aroma. Lagered horizontally for two months for clarity of flavor.', '4.9', '37', '15');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Murder Ballads', 'Porter - Baltic', 'A cold lager fermentation sets this porter apart from its English and American brethren. An amazingly complex dark malt character produces layers of milk chocolate, cherries, plums, and toffee. Full bodied with a touch of sweetness up front, Murder Ballads finishes with a dry coffee roast. 2019 Bronze & 2020 Silver GABF Medal Winner.', '8', '41', '15');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Dearly Departed', 'IPA - Black / Cascadian Dark Ale', 'Cascadian Dark Ales, or Black IPAs, were first brewed in the Pacific Northwest. Ours features Oregon-grown Simcoe hops and dark malts that were steeped in cold water to extract color and a rich dark chocolate character with less roasty bitterness. Simcoe dry hops impart piney and grapefruit aromas backed up by rich malt character and a firm bitterness.', '5.6', '45', '15');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Imperial Cookies And Milk Stout', 'Stout - Russian Imperial', 'Inspired by our Kitchen Sink Cookie, this easy drinking Russian Imp. Stout is brewed with cacao nibs from The Cleveland Chocolate Co., vanilla beans, and oats. Garnish with a mini cookie for $1.', '10.1', '60', '15');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Sweet Potato Saison', 'Farmhouse Ale - Saison', 'Our kitchen roasted more than 150lbs. of sweet potatoes from CWRU''s farm for this fall seasonal. A light touch of cinnamon, fresh ginger, nutmeg, and allspice complement the Belgian saison yeast and the sweet potatoes impart a glowing orange hue.', '7.7', '25', '15');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Sour Raspberry Grisette', 'Grisette', 'Big raspberry aromas greet you in this sessionable Belgian-style saison. The fruit''s sweetness quickly fades to a sour bite from a lactobacillus and brewer''s yeast co-fermentation. Belgian yeast character intermingles with the fruits complexity to create an easy day drinker.', '4.1', '12', '15');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Say When Saison', 'Farmhouse Ale - Saison', 'Belgian style saison brewed with black peppercorns in the boil and fermented with pink and green peppercorns. Complex aroma with a pleasant peppery finish.', '6.9', '26', '15');
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Philthy', 'Hazy IPA', 'A new unfiltered Hazy IPA. People in Philly don’t have a filter, and neither does our Philthy Hazy IPA. This bold, aromatic ale is loaded with tropical and citrus hops character and has that classic hazy, golden color. And just like the city where it’s brewed, this unapologetic IPA is left unfiltered.', '6.5', '30', '16')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Philadelphia Pale Ale', 'Pale Ale', 'Not to be boastful, but we believe that all other ales pale in comparison to this Philadelphia favorite. Dry-hopped with an abundance of distinctive Simcoe hops, this straw-colored pale ale is more drinkable than bitter, more aromatic than aggressive.', '4.6', '37', '16')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Philly Standard', 'Beer', 'Work Hard, Drink Easy. Sometimes you just want a beer. No bells. No whistles. Just a good, handcrafted, no-nonsense beer that works just as hard as you do. Light. Clean. Easy. The standard beer for every occasion.', '4.5', '10', '16')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Our Signature IPA', 'American IPA', 'Our Signature IPA is loaded through and through with assertive American hops. Traditional floor-malted English barley gives IPA its firm malt character while generous amounts of Chinook and Amarillo hops impart aromas of pine and tangerine for a perfectly balanced IPA.', '7.0', '62', '16')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Brawler', 'English Mild', 'Crafted in the style of an English mild, this malt-forward ale is great when you want to go a few rounds. Its smooth character, hints of caramel and toast and remarkable drinkability define this ruby-colored brew as a knockout session ale.', '4.2', '12', '16')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Loyal Lager', 'American Craft Lager', 'Philly''s hometown Lager. 25 years after opening our first garage brewery in Manayunk, we’ve built our dream brewery in the heart of the city – all thanks to our fans who have been loyal since the beginning. As a sign of our gratitude, we’ve used our new world-class brewing system to create Loyal Lager: a crisp, easy-drinking American Craft Lager brewed with two-row malt and aromatic Loral hops. It’s what a clean, high-quality lager is meant to be.', '5.0', '5', '16')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Featherweight', 'Hazy Pale Ale', 'Just because it’s light, doesn’t mean it isn’t good in a fight. Put up your dukes and enjoy this light-bodied, low bitterness hazy pale ale with tangerine and citrus hops. And, coming in at only 105 calories, this is the perfect easy-drinker for any weight class.', '3.7', '20', '16')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Pynk', 'Fruit Beer', 'Each sip of PYNK starts with a crisp, fruity zing and finishes refreshingly dry. The delightful pink hue comes from heaps of fresh raspberries and cherries in every batch. It’s no wonder PYNK is called the rosé of beer. So let’s raise our bottles and pints to the good things in life — like family, friends, and beer — that bring us all together.', '5.5', '6', '16')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Love Stout', 'Nitro Stout', 'Our brewers have poured their hearts and roasted malts into this rich, well-rounded stout. Luscious notes of coffee and chocolate accent Love Stout''s smooth, creamy mouth feel. We will not apologize for any amorous affairs resulting from the consumption of this beverage!', '5.5', '33', '16')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Extra Special Ale', 'English Ale', 'Our first-ever and most-beloved brew, ESA helped establish a following for cask-conditioned beer in the United States back in the ‘90s. East Kent Golding hops give this British-inspired ale a subtle spiciness, which complements the strong malt backbone. ESA is floral, earthy and smooth with hints of chocolate and caramel, rounding out this deep chestnut-colored ale.', '6.0', '47', '16')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Hopshaug', 'NE Double IPA', 'New England style double IPA dry hopped w/ Sultana & Amarillo.', '8.4', '46', '17')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Liquid Hips', 'Hazy IPA', 'Hazy IPA double dry hopped with Galaxy, Waimea & Cashmere delivering a tongue drenching bouquet of orange creamsicle, strawberry, lemongrass, & peach sorbet.', '7.1', '24', '17')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Frux: Raspberry Lemonade', 'Fruited Sour', 'Fruited Sour Ale w/ Raspberry & Lemon Purée. Contains lactose.', '7', '8', '17')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Pink Pineapple Wit', 'Witbier', 'Hundreds of pounds of juicy pineapple, pink guava, & a dash of coriander create this incredibly flavorful, refreshing witbier.', '5.5', '10', '17')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Calumet', 'Saison', 'French saison spiced w/ rainbow peppercorn & coriander. Lightly hopped w/ Lemondrop. Dry w/ a farmhouse funk.', '6.2', '34', '17')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Crumble', 'Oreo Porter', 'A luscious, velvety porter conditioned on 50lbs of Oreo, liquified cacao, & Madagascar bourbon vanilla beans.', '6.2', '36', '17')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Tangerine IPA', 'NE IPA', 'Brewed w/ a massive heap of wheat & flaked oats, then fermented on a fruit-forward, juicy NE yeast strain. Hopped w/ Simcoe & Centennial at nearly 3lbs/BBL, then blended w/ nearly 200lbs of juicy tangerine for an exceptionally juicy, citrusy finish.', '6.9', '44', '17')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Wigman Simcoe', 'NE Pale Ale', 'Our beloved Wigwam double dry hopped w/ Simcoe. Heavy notes of dank pine & mango.', '5.0', '33', '17')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Black Bear', 'IPA', 'Named after our infamous East Falls black bear & brewed to his liking with 60lbs of wildflower honey. Dry hopped with Amarillo for a citrusy finish.', '6.0', '73', '17')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Buzzz''d', 'Wildflower Honey IPA', 'Brewed w/ flaked oats & 60lbs of Wildflower Honey from Fruitwood Orchards in Monroeville, NJ. Kettle & dry hopped w/ Idaho 7. Notes of blueberry, pineapple, & orange zest. Smooth, refreshing, & a bit sweet.', '7.5', '26', '17')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('There''s No Crying In Baseball', 'Hazy Mango IPA', 'A hazy IPA with natural mango flavor and a wicked curveball. Light, hazy, and refreshing, this beer is packed with flavor from hefty dry-hopping and natural mango. You could say it’s in a league of its own.', '6.0', '42', '18')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Purple Monkey Dishwasher', 'Chocolate Peanutbutter Porter', 'If candy and beer had a beautiful liquid baby. Do you like chocolate and peanut butter but hate all that pesky chewing? Give your teeth a vacation with our rich, full-bodied chocolate peanut butter porter.', '6.7', '35', '18')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Stacey''s Mom', 'Citra IPA', 'You’re in love with this beer. One of our year-round flagship beers, Stacy’s Mom is an IPA with low bitterness and lots of hop flavor. It’s dry-hopped entirely with Citra, which, as you may have guessed, gets its name from its strong citrus notes. (You’re so smart!)', '7.5', '85', '18')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('#Adulting', 'Guava IPA', 'A guava IPA that paid its rent on time. Adulting is hard. Finding a delicious IPA should not be. Well, look no further. This smooth IPA is infused with all-natural guava flavor, making it the perfect reward for remembering to turn the oven off.', '6.8', '45', '18')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('I''ll Have What She''s Having', 'Chocolate Hazelnut Imperial Stout', 'This Chocolate Hazelnut Imperial Stout is so rich and decadent, you’ll want to spread it on toast. Our winter seasonal Imperial Stout is infused with natural chocolate and hazelnut flavors. Rich, dark, and full-bodied without being sticky-sweet, this beer is smooth. Like, so smooth. Rob-Thomas-and-Carlos-Santana-on-an-ice-luge smooth.', '9.4', '87', '18')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('I Love Lamp', 'Pineapple Hefeweizen', 'A German wheat beer with a Hawaiian twist. This refreshing German Hefeweizen got taken on a tropical vacation with the addition of natural pineapple flavor. Does this go against the German beer purity law? Yep. Is that the way we like it? Also yep.', '5.5', '12', '18')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Trick Or Treat', 'Chocolate Pumpkin Porter', 'Hey! This beer is haunted! Don’t drink it! Yes, I know it’s a dark, roasty porter with cinnamon, clove, nutmeg, and chocolate, and it tastes amazing, but don’t drink it! It’s! Haunted! Fine. But don’t run to me when the walls of your fridge start bleeding.', '7.8', '35', '18')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('New Phone Who Dis?', 'Caramel Macchiato Porter', 'A caramel-coffee kick in the crotch. Dark and toasty, rich and roasty. Our winter seasonal porter is brewed with dark, sweet malt and infused with natural caramel flavoring and locally roasted Saxby’s coffee. Your favorite espresso beverage is now your favorite adult beverage.', '6', '8', '18')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('#ICan''tEven', 'Watermelon Blonde Ale', 'Like eating a watermelon without all the slicing. Do you want to drink beer and eat watermelon but only want to use one hand? Then this blonde ale infused with natural watermelon flavor is the one for you. And it won’t get your mitts all sticky.', '5.6', '9', '18')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('You''re Killing Me, Smalls!', 'Rasberry Shandy', 'A blonde ale with raspberry, lemon, and attitude. Welcome to our warm-weather crusher. This light, refreshing blonde ale has been infused with natural raspberry and lemon flavors, making it the perfect beverage for stickball season.', '5.5', '14', '18')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Black Cat Lager', 'Black Lager', 'Black Cat Lager is brewed in honor of Nero, one of our resident working cats. This Double Dry Hopped Black Lager is as delicious as black cats are adorable. Cheers to you Nero!', '5.0', '19')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Kenzinger', 'Beer', 'This crisp, light-bodied, golden beer is the very definition of refreshment. The combination of premium German pilsner malts and American Noble hops aims to please your taste buds and satisfy your thirst.', '4.5', '19')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Youth in Asia', 'Imperial IPA', 'An Imperial IPA brewed with rice and sorachi ace hops.', '8.5', '19')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Electro Lyte', 'Pale Ale', 'A very sessionable, full-bodied Pale Ale with a low ABV (3.9%), brewed with apricots, white and pink grapefruit, a touch of Himalayan pink salt and rye malts. It has a very generous amount of aroma hops including Chinook and Nugget. This beer contains more electrolytes than your average brew (namely sodium and potassium) due to the addition of apricots, grapefruits and pink salt which naturally contain a high amount of these minerals. It is the ideal beer to recover from a run with.', '3.9', '19')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Walt Wit', 'White ALe', 'The American poet, Walt Whitman, once portrayed a sunset over Philadelphia as “…a broad tumble of clouds, with much golden haze and profusion of beaming shaft and dazzle”. Pour yourself a bottle of Walt Wit Belgian-Style White Ale and see what he was talking about. A pinch of spice and a whisper of citrus lend complexity to this fragrant and satisfying ale.', '4.5', '19')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Working Cat Pale Ale', 'Pale Ale', 'Working Cat is an ode to Duke, our very own working brewery cat. It is a double dry hopped pale ale brewed with Amarillo and Falconer’s Flight hops, as well as grapefruit and orange peels, which gives the 5% session-able beer balanced citrus notes with minimal bitterness. This newly-released brew is smooth, citrusy and light bodied, and is packed full of aroma.', '5.0', '19')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('PHL Session IPA', 'IPA', 'Philadelphia’s hoppy and light session IPA. PHL has all of the hop aroma and flavor that you would expect from an IPA with the alcohol content of a light beer.', '3.9', '19')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Schwarzinger', 'Black Lager', 'A German style black lager brewed with premium pilsner and dark roasted malts.', '5.0', '19')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Shackamaximum', 'Imperial Stout', 'In 1682 William Penn signed a treaty with the Native American leaders of the Shackamaxon village, which later became the district of Kensington, where our brewery is located today. This is a rich, dark brew made with chocolate and Munich malts and aged on French oak. We hope you enjoy sipping on this full bodied imperial stout.', '8.0', '19')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Kilty Pleasure', 'Scotch Ale', 'Kilty Pleasure Scotch Ale exhibits a subtle aroma of vanilla and boasts a rich six-malt body tempered by heather and hops.', '10.0', '19')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Love City Lager', 'American Golden Lager', 'Love City Lager is brewed for everybody. Our American Golden Lager is simple, clean, and true to its roots. Lightly malty and a hint of orange aromatics give every bright sip a clean landing.', '4.0', '20')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Lime City Lager', 'Lime-Infused Lager', 'That feeling you get when surrounded by good friends & sunshine – in a can! Made with lime and sea salt for a bright, refreshing taste. The perfect beer for warm weather & good times.', '4.0', '20')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Zen Arcade', 'Summer Ale', 'Fermented with Kviek yeast, this refreshing beer is bursting with lemon & berry aroma & flavor. European hops balanced by light bready malt character make this a well-balanced session ale. ', '4.8', '20')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Deep Cut', 'Pilsner', 'Pilsner is the beer that brewers reach for. Like a musician’s favorite track, it’s a Deep Cut. This one is made with all German malt and hops. Lightly bready malt character with floral and herbal hop notes. It’s crisp, complex & crushable.', '5.0', '20')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Sylvie', 'Oatmeal Stout', 'This English-style oatmeal stout is silky smooth and lightly sweet. This year-round stout s brewed with dark, roasted malts then lend rich dark chocolate and espresso flavors. The addition of oats lends a smooth, round finish.', '5.0', '20')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Unity', 'American IPA', 'This approachable IPA brings hop lovers together around the citrus flavors of our hop blend. It is assertive, yet highly drinkable. Low bitterness and a crisp finish.', '5.5', '20')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Eraserhood', 'Hazy IPA', 'Our signature hazy IPA is a love letter to our neighborhood, the Eraserhood. Off-beat and vibrant, this IPA is triple dryhopped. Huge flavors of mango and pineapple with a smooth, round body. Moderate malt backbone and juicy finish.', '7.2', '20')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Periphery', 'Wild Ale', 'A lightly tangy wheat beer, 100% barrel fermented with French Saison yeast & a blend of Brettanomyces yeasts. Dry & bright with a pleasant tartness, this beer has complex notes of lemon rind, oak, & mild green peppercorn.', '6.0', '20')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Better One Or Two', 'Double IPA', 'The original ophthalmic DIPA and first in our arsenal, B12 drips with an ever present cohesion of Citra, Mosaic, and Simcoe that resonate the bold notes of grapefruit, tangerine spritz, ripe red berry, and sticky sap.', '8.0', '21')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Astroid', 'West Coast IPA', 'This West Coast style IPA stands firm alongside it’s hazy core brethren. Thoughtfully hopped with Sultana, Citra, and Chinook, you can expect huge aromas of candied pineapple and orange, with flavor that follows suit alongside sticky pine sap and a clean bitterness on the back end.', '7.2', '21')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Jam', 'IPA', 'Jam is the solo show of hops at center stage. Each variant is dry hopped exclusively with a single variety, allowing that hop to shine and shred. The original Jam features Vic Secret, with Citra, Mosaic, Simcoe, Galaxy, and Nelson rotating through.', '6.8', '21')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Lustra', 'American Pale Ale', 'The one that started DG. Our favorite beer and soon to be yours. This perfect pale ale is super soft and dripping with the sweet nectar of sticky oils from Citra and Amarillo hops, for pungent notes of pink grapefruit, sweet tangerine, and blossoming bouquet.', '5.8', '21')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Prologue', 'American Pale Ale', 'This classic American Pale Ale is thoughtfully hopped with Simcoe, Centennial, Amarillo, and Columbus for bright hints of lime, tangerine, spring blossoms, sticky pine and a firm but very approachable bitterness to round it all out.', '5.8', '21')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Etch', 'IPA', 'Part of our original trio of lupulin cryo beers alongside Pounch and Flock, Etch is hopped with Citra Cryo and Enigma hops for a unique and delicious experience that I can only liken to that of a white shark bite.', '6.6', '21')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Marbled Hearts', 'American Cream Ale', 'A beautiful classic with bright notes of marmalade, grapefruit, and honeysuckle.', '4.5', '21')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Upward Below', 'Petite IPA', 'A petite IPA ideal for anytime at 4% and loaded with Citra and Mosaic hops for layers of pungent grapefruit and sticky sweet berries.', '4.0', '21')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Tentacle Squad', 'IPA', 'A torrent of blended tropical aroma with punches of mango, raspberry, key lime, tangerine, and purple skittle all baked lovingly into grandma’s pie crust recipe.', '7.0', '21')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Aevum', 'Petite IPA', 'Small but mighty, Ella and Enigma hops are on full display in this easy drinking but potent Petite IPA. ABV dialed back, hop suration turned to 11. Just the way we like it.', '4.2', '21')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Lager Roots Lime', 'Lager', 'Lager conditioned on Limes.', '4.9', '22')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Screaming Tangerine Wheat Beer', 'Wheat Beer', 'Wheat Beer Conditioned on Tangerines.', '4.6', '22')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('The Koelaid', 'Sour Ale', 'Fruited Sour Ale With Concord Grape Juice.', '7.8', '22')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Business Casualty DIPA', 'IPA', 'J the DIPA dry hopped with Simcoe, Denali, Crystal and Lemondrop hops.', '8.0', '22')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Sovereign Deity', 'Sour Ale', 'Gose style sour ale aged on Alaea Red Hawaiian sea salt from Steel City Salt Company with lemon and lime peel.', '4.9', '22')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Mutant Temple IPA', 'IPA', 'Mutant Temple is an India Pale Ale dry hopped with Citra Hops. Aroma: Soft lemon zest and subtitle honeysuckle. Body: Light, crisp, effervescent Flavor profile: Notes of oat, lemon, lime, and tropical fruits.', '5.3', '22')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Spiral Bloom', 'Farmhouse Ale', 'Farmhouse Ale Fermented With Native Cultures, Rose Hips and Hibiscus.', '6.0', '22')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Ambient Flux IPA', 'IPA', 'IPA dry hopped with two types of New Zealand hops - Nelson Sauvin and Motueka.', '5.3', '22')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Map Change', 'Smoked Wheat Beer', 'Map Change - A Grodziskie Smoked Wheat Beer. This style historically from Poland, is made with Oak smoked wheat. Golden in color with high carbonation & low ABV. You''ll taste a smooth, smokey flavor, with a dry & crisp finish.', '3.0', '31', '23')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Parking Chair', 'IPA', 'Parking Chair is an imperial double India Pale Ale. High Alcohol by Volume, dry, piney and resinous hop aroma and flavor. Dryness accentuates hop flavor and reduce malt profile.', '9.0', '91', '23')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Curl of the Burl', 'Schwarzbier', 'Our Brewers'' Choice Curl of the Burl is a Schwarzbier. Often referred to as a Black Lager, this German-Style Schwarzbier is drier, darker, & roastier than Penn Dark, it''s Munich Dunkel cousin. Made with Perle Hops and Pilsner, Munich, Cara, & Chocolate Malts. Our latest version was made with Cacao Nibs & Conditioned on Black Forge Costa Rica Roast coffee.', '6.5', '21', '23')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Toxic Lupulinity', 'Hazy IPA', 'Meet Toxic Lupulinity, a New England Style IPA. Made using golden promise malt, oats, flaked wheat, citra, simcoe, and mosaic hops. This is a juicy, hazy, IPA with citrus and piney notes.', '6.7', '40', '23')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Nickalobe Altra', 'Altbier', 'A well-balanced, well-attenuated, bitter yet malty, clean, and smooth, amber-to-copper-colored German beer. his brew hails from the traditional style of beer from Düsseldorf. “Alt” refers to the “old” style of brewing.', '4.7', '32', '23')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Harvest Rye', 'IPA', 'A fall favorite has returned. Harvest Rye IPA is back. This full-bodied IPA is citrus-forward with notes of grapefruit, pine, fresh bread, and has a spicy backbone from the rye malt. Harvest Rye IPA has finishes dry.', '8.4', '55', '23')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Rods and Cones', 'Hoppy Sour', 'Made with Mosaic and Citra hops, giving this beer a huge hop flavor and aroma. You''ll taste notes of lemon, orange rind, and sourdough bread.', '6.0', '55', '23')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('German Chocolate Cake', 'Milk Stout', 'A fresh lactose milk stout brewed with pounds of Betsy Ann Dark Chocolate, it is then finished on shaved, toasted coconut.', '5.5', '25', '23')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Pink Boots Virago''s Fury', 'India Pale Lager', 'Hoppy lager with notes of tropical citrus, stonefruit, pine, on a bready malt backbone. Brewed in collaboration with local female brewers on Pink Boots Brew Day in March using a Pink Boots selected Hop Blend.', '5.5', '55', '23')
INSERT INTO beers (name, beer_image_url, style, description, ABV, IBU, brewery) VALUES('Collaboration Resilence Pale Ale', 'Pale Ale', 'A clean, quaffable beer that is pleasantly hoppy and interesting without being intimidating.As part of the local and international beer community’s ongoing support for bushfire relief efforts in Australia, brewers the world over have joined forces in a fundraising brew. Resilience Beer is a project that arose in the wake of unprecedented bushfires in Australia during late 2019 and early 2020. Brewed at East End Brewing with over 20 local breweries participating.', '5.5', '28', '23')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Big Hop', 'American Ale', 'Loads of Centennial and Cascade hops make this a truly American Ale, brewed to be nice and hoppy but not especially bitter. Balanced with a solid Munich Malt backbone. Our flagship beer, and by far our most popular overall.', '5.8', '24')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Little Hop', 'IPA', 'Just like the name implies, this is the smaller version of our flagship Big Hop beer; lighter on hops, lighter on ABV, still full of flavor. The hop varieties we use for dry hopping on this beer changes with every batch! Perfect for porch parties and barbeques.', '4.4', '24')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Hefe Weizen', 'Lager', 'We''ve come up with the perfect way to enjoy a bright sparkling taste of summer, no matter what the season may be.  This hefeweizen carries a distinct banana ester profile, and crisp finish. No bananas were harmed or peeled in the brewing of this beer. It''s just a little side benefit that our German yeast strain gives us.', '6.2', '24')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Fat Gary Nut Brown Ale', 'Brown Ale', 'Once part of our Session Ale series, and now a year-round beer, this deep brown ale is surprisingly light in body and low in alcohol. Dark malt character, light sweetness and modest alcohol content make it a beer that you can spend the day with and not hurt yourself.', '3.7', '24')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Partly Clahdy NE IPA', 'IPA', 'Our original New England style IPA focusing more on flavor and dry hop additions than on bitterness. Think juicy orange juice, palette softness, and haze. Inspired by those perpetually overcast Pittsburgh skies that we all know and tolerate.', '6.0', '24')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Chance A''Shars Pale Ale', 'Pale Ale', 'The inevitable follow-up to Partly Clahdy, Chance A'' Shahrs is a hazy Pale Ale brewed with Australian and New Zealand hop varieties (Galaxy, Motueka, and Rakau). Please note: the key word here is SHAHRS, or what yinz might call "rain" if you''re not from around here. Not to be confused with “Sahrs.” ', '5.8', '24')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Caution Slippy', 'DIPA', 'The Pittsburghese weather puns continue with Caution Slippy, our dank New England style Double IPA. More juice, less bitter, tastes great! Made with Eureka and Experimental 06297 hops.', '8.2', '24')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Pennsylvania Handshake', 'Milkshake IPA', 'The most ridiculous beer style in the hazy-hoppy category made with our GOOD FRIENDS from Pizza Boy Brewing: a "Milkshake IPA". And by ridiculous, of course I mean ridiculously delicious. Brewed with lactose sugar, and real nectarines,  apricots, and peaches - along with an absurd amount of cold-side addition Citra, Amarillo, and Azacca hops', '7.0', '24')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Saison La Seconde', 'Saison', 'This is our Premiere Saison that’s been fermented with our favorite farmhouse yeast, and then secondarily aged on brettanomyces in red wine barrels for a year.', '6.3', '24')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Eye Opener Coffee Porter', 'Porter', 'The perfect marriage of good coffee and GOOD BEER, just like it is here, at our base of brewing operations on Julius Street. This robust porter is made with coffee from our GOOD FRIENDS (and former roommates) at Commonplace Coffee.', '6.5', '24')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('1890 Lager', 'Lager', 'An American Lager just as American as our beloved Pirates. Light bodied, light in color and just the right amount of flavor to quench any beer drinker’s thirst. This beer pays homage to our first baseball team, the Pittsburgh Burghers.', '4.7', '25')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Wainwright', 'Cream Ale', 'In 1876 Winterton Brewery used to occupy the corner of Wainwright Street, which is now 36th Street, and Smallman Streets. This historic brewery was once located just behind our Lawrenceville location. We look back on this historic Ale brewery and owner Joseph Wainwright and tip our hat with this classic Pre-Prohibition style Cream Ale.', '5.4', '25')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Lock & Dam', 'Wheat Beer', 'Our summer wheat beer springs forth with waves of grain and beautiful citrus notes of lemon grass.', '5.2', '25')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Sweat and Gold', 'IPA', 'An old school throwback to the west coast original IPA. A noticeably malty body supported by Piney, earthy hops with just a touch of citrus to keep it clean.', '7.2', '25')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Farkleberry Tart', 'Sour Ale', 'Berliner Weisse style sour beer infused with blackberry, black currant, and vanilla. Named in honor of Pittsburgh''s KDKA radio man, Jack Bogut whose tireless creativity for radio gags turned into a fundraising legacy benefiting Children''s Hospital. Proceeds from this beer will go to children''s Hospital in the spirit of Jack''s mission.', '6.0', '25')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Jack Rabbit', 'IPA', 'Classic west coast DIPA featuring ripe citrus marmalade notes wrapped in earthy and resinous hop bitter. Its namesake is one of the oldest wooden roller coasters in the world—the Jack Rabbit at Kennywood. Get ready for a double dip on my hop coaster!', '8.6', '25')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Alpha Particles', 'Imperial IPA', 'All the alphas in Azzaca, Citra, and El Dorado were dumped into the smashing cone for this one! Big flavor, big beer! We throwing a shoutout to the former Westinghouse Atom smasher that was located in the Forrest Hills burbs to research peaceful atomic energy production for civilian power. It was used in 1940 to discover the photofission of uranium and thorium.', '8.0', '25')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Yellow Funicular', 'Hefeweizen', 'A smooth ride to summer vistas just like its namesake Mon Incline. Silky soft body of noticeably rich spring red wheat along with a balanced cast of banana, clove, and fruity esters of naval orange, mango, and apricot.', '5.1', '25')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Jack Rabbit', 'Imperial IPA', 'Classic west coast DIPA featuring ripe citrus marmalade notes wrapped in earthy and resinous hop bitter. Its namesake is one of the oldest wooden roller coasters in the world—the Jack Rabbit at Kennywood. Get ready for a double dip on my hop coaster!', '8.6', '25')
INSERT INTO beers (name, beer_image_url, style, description, ABV, brewery) VALUES('Penn Ave. Pale Ale', 'Pale Ale', 'A satisfying American Pale Ale that gives a slight edge to its malty composition over the palate refreshing, slightly bitter and floral hop character. Burgh’ers is just a short trip down Penn Ave to Lawrenceville!', '6.3', '25')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('mvanaskie13', '4', 'Poured into a standard pint glass: color is straw, slight chill haze, fine white head presenting 1 finger thick with some lingering lacing on the glass and thin top. Smell is pleasant malt with notes of biscuit, crackers, toast, light earthy hops with some grassiness. Taste is similar with pleasant pilsner malt, biscuit, crackers, earthy hops, hoping is light but pleasant, not really getting much from the dry-hop. Mouthfeel is moderate carbonation, low/moderate sweetness, low bitterness, light drying crispness. Overall a nice malty pilsner, crisp feel, moderate hopping.', '5/21/21', '1')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Bierschenker', '4', 'Poured into a tall clear pint glass. Appearance is a light yellow with about a finger''s worth of white head with decent retention and good lacing. Aroma: barley, grain, rice, floral and grassy hops, hay, cereal, cracker malt, and white pepper. Somewhat crisp flavor. Tasting some sweet notes like apple and pear. Grassy with floral hops make up most of the body combined with the other notes mentioned. Body is medium-light.', '1/24/21', '1')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Suds', '3', 'Light, golden color beer from a twelve ounce aluminum can. The head is brilliantly white and the liquid has just a slight haze. The aroma is fresh with a grass and grain scent. Clean and crisp flavor, and a very pleasant dryness. Overall, very drinkable.', '10/18/20', '1')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('zeff80', '5', '16oz can. Poured out a clear, straw-yellow color with a good-sized, white head of foam. It left sudsy trails of lace on the glass. It smelled of caramel, toffee and fresh cut hay. It tasted sweet with caramel, toffee and a subtle bitterness. Crisp, sharp and smooth.', '9/5/20', '1')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Jaasoares', '4', 'Pours an amber color with a short lived dirty white head which turns to lacing. Tropical fruit, pine, floral and hop notes on the nose. But magically all those notes combine to give a bubblegum aroma. The taste of grapefruit hits you upfront but quickly leaves a light and mild bitterness which dissipates at the finish. Tbh- this ones overrated.', '5/7/21', '2')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Vetbidder', '3', 'Slightly hazy medium golden with a decent white head makes for an ok look here. Interesting nose with grapefruit and pine trees in there. This has a sort of non ipa fruit flavor with a bitter ipa ending. It’s almost like the start of sweet grapefruit tea with the bite on the end. Thinner feel with less carbonation. This is ok...', '5/3/21', '2')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('jimmah120', '3', 'Typical IPA in look. Smell leans into pine, as well as tart berries, like lingonberries. That''s a unique hop flavor. Very juicy. Maybe the best American IPA I''ve had outside of the USA, but that''s not saying much', '2/20/21', '2')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Sammonsd', '4', 'Solid offering from Brew Dog. The grapefruit punches you in the face with a nice citrus burst. A good easy sipper that sticks out a bit due to the fruit addition.', '12/28/20', '2')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('johnnnnnniee', '4', 'Lightly hazy orange gold with a decent sized bubbly white head. Big bready light caramel toasty maltiness. Earthy lemony citrus with hints of exotic fruit and pine. Decent easy drinking old school haze.', '1/16/21', '3')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('NCSapiens', '3', 'Great choice for introducing my mom to IPAs. Kindly if boring, but nice when looking for something easily drinkable and not too bitter.', '8/13/20', '3')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('SAUbrewcrew', '4', 'Very tasty and refreshing ipa. Super clean taste and feel; actually would not even think it’s abv would be 7.2%. Just a very like able ipa that is much better than the average.', '7/24/20', '3')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Realdeal577', '4', 'Not as hazy as some in this style but good color and nice 1.5 finger head. Aroma of Citrus and Mango. Mouthfeel is very good, medium body and carbonation. Taste follows nose with some pineapple notes, hoppy bitterness in the middle, and a piney/alcohol finish. Very good NEPA-currently one of my favorites!!', '7/1/20', '3')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Gareth W.', '2', 'Definitely pineapple. Not much else I can taste, tbh. Couldn’t drink an awful lot as it’s quite sweet, not cloying, just a tad sweet to drink a lot of.', '6/3/21', '4')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Beerguy13', '3', 'Refreshing pineapple taste but very sweet. Good for summer.', '6/28/21', '4')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('PineappleManiac', '5', 'LOVE THIS! This is the best pineapple beverage ever.', '7/18/21', '4')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('SteveG', '1', 'This beer is gross. I don''t like it. It''s too sweet.', '8/1/21', '4')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('tigg924', '3', 'Pours clear, gold in color with one quarter inch head. Taste is piney and eathy hops with toasted malt. Bitter, light body, high carbonation. An average to just above average Bitter sipper. Not bad but not overly interesting either. Probably my least favorite of Brew Dog''s non alcoholic line.', '1/10/21', '5')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('CanConPhilly', '5', 'Easily the best Brewdog na ipa. Perhaps the best na ipa period. This tastes like a legit ipa. It’s watery, but you can taste the hops, and its noticeably bitter. This was probably even better in October, as I bet the onion/garlic flavor would be absent. I don’t much care for the other Brewdog ipas, but this is on another level. Must try.', '1/20/21', '5')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('VoxRationis', '3', 'Low alcohol beers are always an interesting trade off on the flavor front. Sometimes its all about the fruit, here more all about the bitterness. Admirable try, certainly has amazing color, nice aroma, and solid flavor, but not quite my personal cup of tea.', '5/14/21', '5')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('WoodBrew', '4', 'My bro-in-law and fellow BrewDog stock holder brought this beer to his brother (my other bro-in-law) wedding outside of Nashville. It poured a clear golden with white head that left some lace. The scent had sweet malt notes with pine and citrus highlights. The taste was nicely balanced and easy to drink with sweet malt base with ripe dark fruit presence. The mouthfeel was medium in body and had good carbonation. Overall it is a good beer.', '6/17/21', '6')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('schoolboy', '3', 'This beauty opens pretty yellow with nice, fine bubbles. The aroma is of the old Punk, kind of hoppy, but this is less dry. It''s a bit sweeter, for sure, but that helps, and you can tell it is going to a good cause, as the feel is pretty boozy.', '5/31/21', '6')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Jaasoares', '4', 'Pours a clear light amber color with a white head which quickly turns to lacing. Pineapple and hops on the nose. Juicy taste of tropical fruit with pineapple for the sweetness of a NE style ipa with the mild citrus and bitter character of grapefruit and resin. Very drinkable solid brew.', '5/1/21', '6')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('vinicole', '5', 'Can. Golden barley colour. Dense frothy head which resides as a scummy layer. Citrus on the nose. Mainly grapefruit with some meadow grass and light malt. Tastes as per aroma if somewhat earthy. Alcohol noticeable. Decent bitter bite to finish. Boozy feel. Heavy metal punk.', '9/11/20', '6')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Jaasoares', '4', 'Pours a mildly hazy pale amber color with a white head. A blast of grapefruit and orange on the nose...promises a lot! Taste disappointingly doesn’t live up...bitterness of grapefruit and orange zest to go with some hops but not much flavor to this one. good to try and then never again.', '5/5/21', '7')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('schoolboy', '3', 'Not recommended except for having some light brews around for drinkers who don''t want more serious tastes.', '4/11/21', '7')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Brewy21', '5', 'Awesome beer. Mild and easy to drink. I would recommend.', '6/1/21', '8')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Sam H.', '4', 'Pretty beer with a nice color. Very tasty. This is in my top 10 personal favorites.', '7/12/21', '8')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('VoxRationis', '4', 'Enjoyable, if almost a bit contrived: hard to conceive that a NEIPA depending on natural peach flavor would hit the mark this closely.', '7/3/21', '9')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Jason G.', '4', 'Wow! Is this a light beer? Brewdog is ahead of it’s time in low cal beer brewing. Pours a hazy straw color with a big foamy white head. Aroma of peaches, citrus and hops. Taste is of peach and mild hops bitterness comes through. This is an amazing brew for just 99 cals. Kudos to brewdog', '5/2/21', '9')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('BEERchitect', '5', 'Medium light in body, Light Speed trends pleasantly dry on the finish with a high sense of drinkability and refreshment. Its a charming little beer that chooses crispness as a balancing agent against the malts as much as its does bitterness.', '2/5/21', '9')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Lucas F.', '4', 'Slightly sour from the berries first bite mixed with the apple goodness.May I add the can presentation is killer. Well balanced hard cider.', '5/1/21', '10')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Vanessa H.', '5', 'Delicious, would recommend! Really cool can label.', '6/29/21', '10')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('George M.', '4', 'Has some zip to it, great taste, and easy to drink. Very good. Try this beer.', '2/16/21', '11')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('HopHunterCL', '4', 'Pours almost black with a thick sticky tan head. Aroma of dark chocolate, vanilla and cherry. I really wanted to like this. I’m a big fan of Seventh Son stouts. This one just didn’t work for me. I feel like the cherry flavor detracts from the base beer rather than enhancing it. Overall I enjoyed this but this brewery does much better in my opinion.', '7/2/21', '12')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('SawDog505', '3', 'Mouthfeel is pretty big, gentle carbonation kind of creamy, a tad dry, and at 11% it may have a little alcohol in the finish, but still easy drinking', '6/1/21', '12')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('jzellinger', '4', 'Cherry Stouts are hit or miss with me, this is one of the better ones.', '5/19/21', '12')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('buschbeer', '4', 'This is a tasty, refreshing beverage. The finish is just dry enough for me to crave another sip as soon as I finish the previous one.', '8/11/2018', '13')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('jrc1093', '4', 'This was a solid tart IPA; maybe a little too balanced for it''s own good, it hangs it''s hat on its more refreshing qualities rather than being an outright flavor bomb. An approachable sour-style IPA for a hot day.', '8/5/18', '13')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('hellobeer', '5', 'There''s definite warming on the way down but it never detracts from the overall experience. I''m a barrel aged fan but never in favor of Brandy Barrels, Seventh Son got it right with blending. Here''s an under the radar beer that''s worth pursuing right here.', '4/10/21', '14')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('brutaltruth', '4', 'Flavors pack the punch with dark bakers chocolate, mulled apples, subtle wafting cinnamon, dark coffee, astringent booze notes, roasted malts, and char. Very nice mouth feel; full body, smooth, rich, lightly coating, lightly warming, and finishing with a very nice bitter malt note.', '3/17/21', '14')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Samuel S.', '4', 'A good summer time beer.', '6/15/21', '15')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Julie M.', '4', 'Great "sitting in the pool" drink.', '7/7/21', '15')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Edward D.', '5', 'Nice, very natural-tasting lime flavor in a light what beer. Tart from the lime.', '8/2/21', '15')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('BB1313', '5', 'This is a nice brew. It''s extremely clean and crisp. There''s something about this one that I really like. It''s simple, but in a really good way. It has a "red ale/hoppy red ale" vibe going on and probably isn''t what you think of when you hear "Strong Ale", probably because of the "lower" ABV for the style. The craftsmanship is on point and really takes this beer to the next level. Well done. I really like it.', '9/2/20', '16')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Lembeh', '4', 'This is a great beer. Great job by Seventh Son on this style.', '5/6/16', '16')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('md3kcn', '3', 'A clean-cut refreshing summer ale for sure. Would love to enjoy this alongside a juicy hamburger and steak fries. Wish there were more malt notes, but I get what it''s going for. Will buy again.', '5/2/21', '17')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('ChrisMon02', '4', 'A decent ipa. Nothing really stands out but it gets the job done.', '12/7/18', '18')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('McBuck', '4', 'Poured down glass from 16 oz can. Decent head and good lacing. Color is light caramel with little cloudy. Smell is nice floral. Flavor is piney, tea tones, little bit of grapefruit, with a crisp, bitter finish. This is a great IPA from Columbus.', '10/12/18', '18')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('WoodBrew', '4', 'I got a crowler of this from the Daily Growler here in Powell, OH. It poured a clear rich mahogany with off white that is not leaving any lace. The scent had rich malt and yeast notes. The taste was nicely balanced and easy to drink with sweet malt base. The mouthfeel was lighter in body with good carbonation. Overall its a solid beer.', '2/7/19', '19')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('jrc1093', '5', 'Solid brown requiring minimal effort to drink; very sessionable without becoming too light.', '8/9/18', '19')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('chinchill', '4', 'Opaque dark brown body with a good beige head. Exceptionally appealing, malty aroma with a a good bit of dark chocolate in the mix. The flavor is almost as good as the aroma. Low bitterness. Very smooth, medium body with light carbonation.', '8/3/18', '19')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Sue S.', '4', 'The taste was nicely balanced and easy to drink with bitter hop base and subtle citrus highlights. The mouthfeel was lighter in body with good carbonation. Overall its a good beer.', '11/23/19', '20')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('bluejacket74', '3', 'It is easy enough to drink. It is nothing special.', '4/13/18', '20')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('WoodBrew', '4', 'I got this off the single shelf at my local Giant Eagle. It poured a clear yellow with white head that is not leaving any lace . The scent had faint malt. The taste was nicely balanced and easy to drink. The mouthfeel was light and creamy with good carbonation. Overall it is a good beer.', '7/13/18', '21')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Josholson666', '5', 'Really nice cream, one of the truest to style I''ve ever had!', '3/26/17', '21')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('tone77', '3', 'Smell is of coffee, little else. Taste is also dominated by coffee, light malts are present. Feels medium bodied in the mouth and overall is a decent beer. However, it''s not what I expect from this style.', '7/18/21', '22')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Rebecca', '4', 'A Tavour treat. Pours a golden color with a minimal head that disappears quickly. Aroma is coffee. Tastes of sweet vanilla and black coffee. Slightly bitter at the finish. Good grass cutting beer.', '1/16/21', '22')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Bob T.', '3', 'This is an okay IPA. It''s not bad or anything but it''s nothing crazy.', '3/6/21', '23')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('WMJ37', '4', 'I don''t have a lot of the fancy descriptions, but this is my current go to beer, and I''m excited it''s local. Comes in cans now, which is great. I like the citrus/floral IPAs, and this fits the bill perfectly.', '9/26/20', '23')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('metter98', '4', 'I liked the aromas and the flavors of the Amarillo hops in the taste of this beer.', '11/28/19', '23')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Benjamin S.', '5', 'I''ll take 100 of these please.', '8/1/21', '24')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Stacey G.', '4', 'It poured a clear golden with no head and no lace. The scent had grain and malt mash notes. The taste was nicely balanced and easy to drink with cracker and yeast start and subtle crisp hop finish. The mouthfeel was lighter in body with good carbonation. Overall it is a decent beer.', '7/23/21', '24')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('oldmanwithabeer', '5', 'This beer makes me excited to revisit the brewery post covid on my next trip!', '6/24/21', '25')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('trevormajor', '5', 'A great beer, probably the beer I drink more than any other. When I think of an IPA, it is this beer that comes to mind', '1/1/21', '26')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('DrBeergd', '4', 'Brilliant. If you''re at a party and you put this in the fridge, nobody else will touch it. It has alot of flavor. Not entirely over the top. There are lighter IPAs available for those who wish it.', '7/30/20', '26')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('SawDog25', '4', 'Overall this is an excellent East-West DIPA, one of the best non NEDIPAs I have had in a long time.', '4/13/21', '27')
INSERT INTO beer_reviews (reviewer_name, reviewer_rating, review_description, review_date, beer) VALUES ('Q-Daddy', '2', 'This beer sucks. I don''t know why it has a high rating at all. I was stuck with a 6 pack of this trash. The Beatles would be ashamed, and hopefully the brewer''s parents are too.', '8/05/21', '96')
