-- Create Tables
CREATE TABLE users (
	user_id int IDENTITY(1,1) NOT NULL,
	username varchar(50) NOT NULL,
	password_hash varchar(200) NOT NULL,
	salt varchar(200) NOT NULL,
	user_role varchar(50) NOT NULL
	CONSTRAINT PK_user PRIMARY KEY (user_id)
);

CREATE TABLE breweries (
	brewery_id int IDENTITY(1,1) NOT NULL,
	brewery_name varchar(50) NOT NULL,
	brewery_description varchar(500) NOT NULL,
	image_url nvarchar(2400) NULL,
	CONSTRAINT PK_brewery PRIMARY KEY (brewery_id)
);

CREATE TABLE address (
	address_id int IDENTITY(1,1) NOT NULL,
	city_name varchar(50) NOT NULL,
	street_number varchar(500) NOT NULL,
	street_name varchar(100) NOT NULL,
	state varchar(20) NOT NULL,
	zip_code int NOT NULL,
	phone_number varchar(20) NOT NULL,
	brewery int NOT NULL,
	url nvarchar(100) NOT NULL,
	map_url nvarchar(200) NULL,
	CONSTRAINT PK_address PRIMARY KEY (address_id),
	FOREIGN KEY (brewery) REFERENCES breweries(brewery_id)
);

CREATE TABLE beers (
	beer_id int IDENTITY(1,1) NOT NULL,
	name varchar(100) NOT NULL,
	style varchar(100) NOT NULL,
	description varchar(500) NOT NULL,
	beer_image_url nvarchar(500) NULL,
	ABV decimal NOT NULL,
	IBU int NULL,
	brewery int NOT NULL,
	CONSTRAINT PK_beers PRIMARY KEY (beer_id),
	FOREIGN KEY (brewery) REFERENCES breweries(brewery_id)
);

CREATE TABLE beer_reviews (
	review_id int IDENTITY(1,1) NOT NULL,
	reviewer_name varchar(100) NOT NULL,
	reviewer_rating int NOT NULL,
	review_description varchar(1000) NOT NULL,
	review_date DATETIME DEFAULT GetDate(),
	beer int NOT NULL,
	CONSTRAINT PK_beer_reviews PRIMARY KEY (review_id),
	FOREIGN KEY (beer) REFERENCES beers(beer_id)
);


